mapper.update:
    UPDATE PS_ATTENDANCE
	SET PSA_IN =#{inTime},
	PSA_APPLIED_IN = #{appliedIN},
	PSA_STATUS=#{psStatus}
	WHERE PSA_EMP_NO = #{empNo}

    UPDATE PS_ATTENDANCE SET
	PSA_APPLIED_OUT = #{appliedOut},
	PSA_APPLIED_IN = #{appliedIN},
	PSA_STATUS=#{psStatus},
	PSA_WORKINGTIME=#{workingTime},
	PSA_OVERTIME=#{overTime},
	PSA_TOTAL=#{total}		
	WHERE PSA_EMP_NO = #{empNo}
	AND PSA_CREATEDATE LIKE #{vacation}
