mapper.select:
SELECT COUNT(*)
FROM DAILY_REPORT
WHERE DR_WRITER_NO = #{ loginUser }
AND DR_RECEIVER_NO = #{ loginUser }
AND DR_CREATE_DATE = SYSDATE
AND DR_STATUS = 'TS'

mapper.update:
UPDATE DAILY_REPORT
SET DR_TITLE = #{drTitle}
AND DR_CONTENT = #{drContent}
AND DR_CREATE_DATE = SYSDATE
AND DR_ATTACH_ORIGIN = #{drAttachOrigin}
AND DR_ATTACH_CHANGE = #{drAttachChange}
AND DR_CATEGORY = #{drCategory}
AND DR_STATUS = 'Y'
