mapper.select:
SELECT COUNT(*)
FROM DAILY_REPORT
WHERE DR_WRITER_NO = #{ drWriterNo }
AND DR_RECEIVER_NO = #{ drWriterNo }
AND DR_CREATE_DATE LIKE SYSDATE
AND DR_STATUS = 'TS'

SELECT *
FROM DAILY_REPORT
WHERE DR_WRITER_NO = #{ empNo }
AND DR_RECEIVER_NO = #{ empNo }
AND DR_CREATE_DATE LIKE SYSDATE
AND DR_STATUS = 'TS'

SELECT COUNT(*)
FROM DAILY_REPORT
WHERE DR_WRITER_NO = #{ drWriterNo }
AND DR_RECEIVER_NO = #{ drReceiverNo }
AND DR_CREATE_DATE LIKE SYSDATE
AND DR_STATUS = 'SN'

mapper.update:
UPDATE DAILY_REPORT
SET DR_REF = #{ drRef }
WHERE DR_WRITER_NO = #{ drWriterNo }
AND DR_RECEIVER_NO = #{ drReceiverNo }
AND DR_CREATE_DATE LIKE SYSDATE
AND DR_STATUS = 'SN'

mapper.select:
SELECT COUNT(*)
FROM DAILY_REPORT
WHERE (DR_RECEIVER_NO = #{drWriterNo}
	OR DR_REF = #{drWriterNo})
AND DR_WRITER_NO != #{drWriterNo}
AND DR_CATEGORY = #{drCategory}
AND DR_CREATE_DATE LIKE SYSDATE
AND DR_STATUS = 'SN'

SELECT COUNT(*)
FROM DAILY_REPORT
WHERE (DR_RECEIVER_NO = #{drWriterNo}
	OR DR_REF = #{drWriterNo})
AND DR_WRITER_NO != #{drWriterNo}


		AND DR_CATEGORY = #{drCategory}




		AND DR_CREATE_DATE LIKE SYSDATE



		AND DR_CREATE_DATE > TRUNC(SYSDATE) AND DR_CREATE_DATE < SYSDATE + 7




		AND DR_CREATE_DATE > TRUNC(SYSDATE) AND DR_CREATE_DATE < ADD_MONTHS(SYSDATE, +1) 




		AND DR_CREATE_DATE > TRUNC(SYSDATE) AND DR_CREATE_DATE < ADD_MONTHS(SYSDATE, +3) 



		AND DR_CREATE_DATE BETWEEN #{startDate} AND #{endDate}


AND DR_STATUS = 'SN'

SELECT A.*, B.EMP_NAME, B.DEPT_CODE, B.JOB_CODE, D.JOB_NAME, C.DEPT_UNAME, C.DEPT_DNAME FROM DAILY_REPORT A JOIN EMPLOYEE B ON B.EMP_NO = A.DR_WRITER_NO JOIN DEPT C ON C.DEPT_CODE = B.DEPT_CODE JOIN JOB_LEVEL D ON D.JOB_CODE = B.JOB_CODE WHERE (A.DR_RECEIVER_NO = #{drWriterNo} OR A.DR_REF = #{drWriterNo}) AND A.DR_WRITER_NO != #{drWriterNo}
AND DR_CATEGORY = #{drCategory}
AND DR_CREATE_DATE LIKE SYSDATE
AND DR_CREATE_DATE > TRUNC(SYSDATE) AND DR_CREATE_DATE < SYSDATE + 7
AND DR_CREATE_DATE > TRUNC(SYSDATE) AND DR_CREATE_DATE < ADD_MONTHS(SYSDATE, +1)
AND DR_CREATE_DATE > TRUNC(SYSDATE) AND DR_CREATE_DATE < ADD_MONTHS(SYSDATE, +3)
AND DR_CREATE_DATE BETWEEN #{startDate} AND #{endDate}
AND A.DR_STATUS = 'SN' ORDER BY DR_NO DESC

SELECT A.*, B.EMP_NAME, B.DEPT_CODE, B.JOB_CODE,
         D.JOB_NAME, C.DEPT_UNAME, C.DEPT_DNAME
FROM DAILY_REPORT A
JOIN EMPLOYEE B ON B.EMP_NO = A.DR_WRITER_NO
JOIN DEPT C ON C.DEPT_CODE = B.DEPT_CODE
JOIN JOB_LEVEL D ON D.JOB_CODE = B.JOB_CODE
WHERE A.DR_WRITER_NO = #{drWriterNo}
AND A.DR_RECEIVER_NO = #{drWriterNo}
AND A.DR_CREATE_DATE LIKE #{startDate}
AND A.DR_STATUS = 'SN'

UPDATE DAILY_REPORT_REPLY
SET DR_REP_STATUS = 'N'
WHERE DR_REF_NO = #{drNo}
