mapper.update:
UPDATE NOTICE
SET NT_COUNT = NT_COUNT+1
WHERE NT_NO = #{bno}

UPDATE NOTICE
SET NT_TITLE=#{noticeTitle}, NT_CONTENT=#{noticeContent},
    NT_AT=#{originName}, NT_CHANGE_AT=#{changeName}, NT_STATUS=#{nStatus}
WHERE NT_NO=#{noticeNo}

UPDATE NOTICE
SET NT_STATUS = 'N'
WHERE NT_NO = #{noticeNo}
